using UnityEngine;
using System;
//TODO make conditional namespace - need to develop more
namespace BoomSports.Prototype
{
    /// <summary>
    /// Used as a container to pass information between object group manager and evaluation for conditions applied to object group manager
    /// </summary>
    [Serializable]
    public struct SymbolObjectGroupEvaluatorContainer
    {
        /// <summary>
        /// Symbol ID to evaluate
        /// </summary>
        [SerializeField]
        public int symbolID;
        /// <summary>
        /// Raw Index in End Display Sequence
        /// </summary>
        [SerializeField]
        public int indexInEndSequence;
        /// <summary>
        /// Used to hold the padding and active vs inactive display zones - Cash Crossing Specific offsets index when sending index in path to account for index of animators affected
        /// </summary>
        [SerializeField]
        public ConfigurationDisplayZonesStruct objectGroupManagerDisplayZones;

        public SymbolObjectGroupEvaluatorContainer(int symbolID, int indexInEndSequence, ConfigurationDisplayZonesStruct objectGroupManagerDisplayZones)
        {
            this.symbolID = symbolID;
            this.indexInEndSequence = indexInEndSequence;
            this.objectGroupManagerDisplayZones = objectGroupManagerDisplayZones;
        }
    }
}
